int dx[] = {1, 0, -1, 0};
int dy[] = {0, -1, 0, 1};

vector<vector<int>> rotateVector(vector<vector<int>> &v) {
    int m = v.size();
    int n = v[0].size();
    vector<vector<int>> tmp(n, vector<int>(m, 0));

    for (int y = 0; y < n; y++) {
        for (int x = 0; x < m; x++) {
            tmp[y][x] = v[m - x - 1][y];
        }
    }

    return tmp;
}

vector<vector<int>> getParts(vector<vector<int>> v, int vx, int vy, int n,
